import React, { useEffect, useState } from 'react';
import '../../Style.css';
import { Box, Typography, Button, Modal, TextField, IconButton, Autocomplete, Tab, Tabs } from '@mui/material';
import axios from 'axios';
import { styled } from '@mui/material/styles';
import SearchIcon from '@mui/icons-material/Search';
import CloseIcon from '@mui/icons-material/Close';
import ChevronRight from '@mui/icons-material/ChevronRight';
import EditIcon from '@mui/icons-material/Edit';

const AddButton = styled(Button)({
  borderRadius: 3,
  padding: '21px 20px',
  boxShadow: 'none',
  textTransform: 'none',
  fontSize: 16,
  backgroundColor: '#0f72bd',
  lineHeight: 1.5,
  borderColor: '#0f72bd',
  color: '#ffffff',
  fontFamily: [
    '-apple-system',
    'BlinkMacSystemFont',
    '"Segoe UI"',
    'Roboto',
    '"Helvetica Neue"',
    'Arial',
    'sans-serif',
    '"Apple Color Emoji"',
    '"Segoe UI Emoji"',
    '"Segoe UI Symbol"',
  ].join(','),
  '&:hover': {
    backgroundColor: '#004f8a',
    borderColor: '#004f8a',
    boxShadow: 'none',
  },
  '&:active': {
    boxShadow: 'none',
    backgroundColor: '#0f72bd',
    borderColor: '#0f72bd',
  },
  '&:focus': {
    boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
  },
});
const CancelButton = styled(Button)({
  borderRadius: 3,
  padding: '21px 20px',
  boxShadow: 'none',
  textTransform: 'none',
  fontSize: 16,
  backgroundColor: '#ededed',
  lineHeight: 1.5,
  borderColor: '#0f72bd',
  color: '#505e71',
  fontFamily: [
    '-apple-system',
    'BlinkMacSystemFont',
    '"Segoe UI"',
    'Roboto',
    '"Helvetica Neue"',
    'Arial',
    'sans-serif',
    '"Apple Color Emoji"',
    '"Segoe UI Emoji"',
    '"Segoe UI Symbol"',
  ].join(','),
  '&:hover': {
    backgroundColor: '#e0e0e0',
    borderColor: '#e0e0e0',
    boxShadow: 'none',
  },
  '&:active': {
    boxShadow: 'none',
    backgroundColor: '#e0e0e0',
    borderColor: '#e0e0e0',
  },
  '&:focus': {
    boxShadow: '0 0 0 0.2rem rgba(0,123,255,.5)',
  },
});
const ListButton = styled(Button)({
  borderRadius: 0,
  width:'100%',
  padding: '21px 50px',
  justifyContent: 'space-between',
  boxShadow: 'none',
  textTransform: 'none',
  fontSize: 20,
  fontWeight: '300',
  backgroundColor: '#fffff',
  lineHeight: 1.5,
  borderColor: '#0f72bd',
  color: '#505e71',
  fontFamily: [
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
  ].join(','),
  '&:hover': {
      backgroundColor: '#e0e0e0',
      borderColor: '#e0e0e0',
      boxShadow: 'none',
  }
});
const AntTabs = styled(Tabs)({
  display: 'flex',
  height: '300px',
  '& .MuiTabs-indicator': {
    backgroundColor: '#1890ff',
  },
});

const AntTab = styled((props) => <Tab disableRipple {...props} />)(({ theme }) => ({
  minHeight: '100px',
  borderBottom: '1px solid #edf2f6',
  textTransform: 'none',
  minWidth: '415px',
  fontWeight: theme.typography.fontWeightRegular,
  marginRight: theme.spacing(1),
  color: '#7f8fa4',
  img: {
    filter: "invert(73%) sepia(6%) saturate(1288%) hue-rotate(175deg) brightness(98%) contrast(82%)"
  },
  fontFamily: [
    '-apple-system',
    'BlinkMacSystemFont',
    '"Segoe UI"',
    'Roboto',
    '"Helvetica Neue"',
    'Arial',
    'sans-serif',
    '"Apple Color Emoji"',
    '"Segoe UI Emoji"',
    '"Segoe UI Symbol"',
  ].join(','),
  '&:hover': {
    backgroundColor: '#f9fbfd',
    boxShadow: "10px 0 5px -5px rgba(103, 162, 206, 0.05) inset"
  },
  '&.Mui-selected': {
    color: '#0f72bd',
    img: {
      filter: "invert(29%) sepia(100%) saturate(1141%) hue-rotate(181deg) brightness(96%) contrast(88%)"
    },
    fontWeight: theme.typography.fontWeightMedium,
  },
}));
const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: '100%',
  height: '100%',
  backgroundColor: 'white',
};

function DeliveryBays() {
  const [tabIndex, setTabIndex] = useState(0);
  const handleChange = (event, newValue) => {
    setTabIndex(newValue);
  };

  const [craneAssets, setCraneAssets] = useState([]);

  const [assetID, setAssetID] = useState();
  const [assetName, setAssetName] = useState('');
  const [assetMaxLength, setAssetMaxLength] = useState('');
  const [assetUnit, setAssetUnit] = useState('');

  const [customName, setCustomName] = useState();
  const [maxLength, setMaxLength] = useState();
  const [unit, setUnit] = useState();
  const options = [
    'metres',
    'cm'
  ];

  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const [openSetName, setOpenSetName] = useState(false);
  const handleOpenSetName = () => setOpenSetName(true);
  const handleCloseSetName = () => setOpenSetName(false);

  const showAssets = () => {
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${localStorage.getItem('token')}`,
    };
    try {
      axios.get('http://127.0.0.1:8000/api/assets/bays', {
        headers: headers
      })
      .then((res) => {
        setCraneAssets(res.data.data);
      })
    } catch(e) {
      console.log(e);
    }
  }
  const showAsset = (id) => {
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${localStorage.getItem('token')}`,
    };
    try {
      axios.get('http://127.0.0.1:8000/api/asset/'+id, {
        headers: headers
      })
      .then((res) => {
        setAssetID(res.data.data.id);
        setAssetName(res.data.data.custom_name);
        setCustomName(res.data.data.custom_name);

        setAssetMaxLength(res.data.data.max_length);
        setMaxLength(res.data.data.max_length);

        setAssetUnit(res.data.data.unit);
        setUnit(res.data.data.unit);

        setTabIndex("1");
      })
    } catch(e) {
      console.log(e);
    }
  }
  const showAsset_onLoad = () => {
    const headers = {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${localStorage.getItem('token')}`,
    };
    try {
      axios.get('http://127.0.0.1:8000/api/assets/bays', {
        headers: headers
      })
      .then((res) => {
        setAssetID(res.data.data[0].id);
        showAsset(res.data.data[0].id);
      })
    } catch(e) {
      console.log(e);
    }
  }
